import base64

# Pega aquí tu código Base64
encoded_code = """"""  # Copia aquí el valor completo

# Decodifica el código Base64
try:
    decoded_code = base64.b64decode(encoded_code).decode('utf-8')
    print("Decoded Code:")
    print(decoded_code)
except Exception as e:
    print(f"Error decoding Base64: {e}")
